INSERT INTO `tour` (`IDTour`, `NameTour`, `DepartureDay`, `Plan`, `Howlong`, `Empty`, `Image`, `Classify`, `Money`, `continents`, `Area`) VALUES
(1, 'Du lịch Hà Nội - Lào Cai - SaPa - Hạ Long', '2019-11-10', '<h3><small><big><strong>NG&Agrave;Y 1: TP. HCM - H&Agrave; NỘI - ĐƯỜNG CAO TỐC - L&Agrave;O CAI - SAPA &nbsp;(Ăn trưa, chiều)</strong></big></small></h3>\r\n\r\n<h3><small><big>Buổi s&aacute;ng, tập trung tại Cổng D4&nbsp;- Ga đi trong nước - S&acirc;n bay T&acirc;n Sơn Nhất đ&aacute;p chuyến bay&nbsp;<em><strong>VJ 126 l&uacute;c 07:10 hoặc VJ128 l&uacute;c 08:15 t&ugrave;y ng&agrave;y</strong></em>. Đ&aacute;p xuống s&acirc;n bay Nội B&agrave;i, khởi h&agrave;nh đến L&agrave;o Cai. Đến L&agrave;o Cai tiếp tục l&ecirc;n Sapa. Chi&ecirc;m ngưỡng&nbsp;<em><strong>d&atilde;y Ho&agrave;ng Li&ecirc;n Sơn</strong></em>&nbsp;tr&ecirc;n đường đi. Đến Sapa v&agrave;o&nbsp;<strong><em>thung lũng Mường Hoa</em></strong>, thăm&nbsp;<strong><em>bản Lao Chải - Tả Van&nbsp;</em></strong>của người M&ocirc;ng, Gi&aacute;y. Buổi tối tự do dạo&nbsp;<strong><em>chợ Sapa</em></strong>. Nghỉ đ&ecirc;m tại Sapa.</big></small></h3>\r\n\r\n<blockquote>\r\n<h3><small><span class=\"marker\"><big><img class=\"right\" src=\"https://bizweb.dktcdn.net/100/299/077/products/5.jpg?v=1529553698327\" style=\"float:left; width:100%\" /></big></span></small></h3>\r\n</blockquote>\r\n\r\n<h3>&nbsp;</h3>\r\n\r\n<h3>&nbsp;</h3>\r\n\r\n<h3>&nbsp;</h3>\r\n\r\n<h3>&nbsp;</h3>\r\n\r\n<h3>&nbsp;</h3>\r\n\r\n<h3>&nbsp;</h3>\r\n\r\n<h3>&nbsp;</h3>\r\n\r\n<h3>&nbsp;</h3>\r\n\r\n<h3>&nbsp;</h3>\r\n\r\n<h3>&nbsp;</h3>\r\n\r\n<h3>&nbsp;</h3>\r\n\r\n<h3>&nbsp;</h3>\r\n\r\n<h3>&nbsp;</h3>\r\n\r\n<h3><small><big><strong>NG&Agrave;Y 2: SAPA - L&Agrave;O CAI &nbsp;(Ăn s&aacute;ng, trưa, chiều)</strong><br />\r\nBuổi s&aacute;ng, chinh phục&nbsp;<strong><em>n&uacute;i H&agrave;m Rồng</em></strong>, thưởng ngoạn<strong><em>&nbsp;vườn lan, vườn hoa &ocirc;n đới, vườn đ&agrave;o v&agrave; khu vườn đ&aacute;</em></strong>, xem biểu diễn<strong><em>&nbsp;m&uacute;a h&aacute;t d&acirc;n gian của c&aacute;c d&acirc;n tộc thiểu số</em></strong>, ngắm to&agrave;n cảnh<strong><em>&nbsp;Sapa v&agrave; &ldquo;N&oacute;c nh&agrave; Đ&ocirc;ng Dương&rdquo; - đỉnh Fansipan</em></strong>&nbsp;tại s&acirc;n m&acirc;y.</big></small></h3>\r\n\r\n<h3><small><big><strong>Lựa chọn&nbsp;</strong>(tự t&uacute;c chi ph&iacute; tham quan) Xe đưa qu&yacute; kh&aacute;ch đến tham quan&nbsp;<strong>khu du lịch c&aacute;p treo Fansipan&nbsp;</strong>với hệ thống c&aacute;p treo 3 d&acirc;y hiện đại vừa kh&aacute;nh th&agrave;nh th&aacute;ng 2/2016. Trải nghiệm cảm gi&aacute;c đi giữa biển m&acirc;y, ngắm to&agrave;n cảnh Sapa v&agrave; thung lũng Mường Hoa, vườn quốc gia Ho&agrave;ng Li&ecirc;n từ tr&ecirc;n cao. Viếng&nbsp;<em><strong>ch&ugrave;a Bảo An</strong></em>, vượt gần 600 bậc thang, chinh phục<em><strong>&nbsp;&ldquo;N&oacute;c nh&agrave; Đ&ocirc;ng Dương&rdquo; - đỉnh Fansipan</strong></em>&nbsp;ở độ cao 3,143m.</big></small></h3>\r\n\r\n<h3><small><big>Xe đ&oacute;n v&agrave; đưa cả đo&agrave;n trở về L&agrave;o Cai. Tự do kh&aacute;m ph&aacute; TP. L&agrave;o Cai về đ&ecirc;m. Nghỉ đ&ecirc;m tại L&agrave;o Cai</big></small></h3>\r\n\r\n<h3><small><big><strong>NG&Agrave;Y 3: L&Agrave;O CAI - H&Agrave; NỘI &nbsp;(Ăn s&aacute;ng, trưa, chiều)</strong><br />\r\nBuổi s&aacute;ng, trả ph&ograve;ng. Tham quan<strong><em>&nbsp;cửa khẩu Quốc tế L&agrave;o Cai</em></strong>, mua sắm tại chợ Cốc Lếu. Về đến Thủ đ&ocirc; tham quan<strong><em>&nbsp;Hồ Ho&agrave;n Kiếm</em></strong>, viếng<em><strong>&nbsp;đền Ngọc Sơn</strong></em>. Nhận ph&ograve;ng. Buổi t&ocirc;́i, tự do dạo bộ dọc theo&nbsp;<strong><em>&ldquo;36 phố phường</em></strong>&rdquo;- khu phố cổ với những ng&agrave;nh nghề đặc trưng v&agrave; truyền thống của cư d&acirc;n Thủ đ&ocirc;. Nghỉ đ&ecirc;m tại H&agrave; Nội.</big></small></h3>\r\n\r\n<h3><small><big><strong>NG&Agrave;Y 4: H&Agrave; NỘI - HẠ LONG &nbsp;(Ăn s&aacute;ng, trưa, chiều)</strong><br />\r\nĐến tham quan<em><strong>&nbsp;Quảng trường Ba Đ&igrave;nh, khu di t&iacute;ch Phủ Chủ tịch</strong></em>. Ngắm&nbsp;<em><strong>hồ T&acirc;y, hồ Tr&uacute;c Bạch</strong></em>, viếng<strong><em>&nbsp;ch&ugrave;a Trấn Quốc</em></strong>&nbsp;- danh lam cổ tự được x&acirc;y dựng từ thế kỷ VI. Khởi h&agrave;nh đến Hạ Long. Tham quan cầu B&atilde;i Ch&aacute;y. Buổi chiều tối tự do thăm th&uacute; phố biển Hạ Long. Nghỉ đ&ecirc;m tại Hạ Long.</big></small></h3>\r\n\r\n<h3><small><big><strong>Lựa chọn</strong>&nbsp;(tự t&uacute;c chi ph&iacute; di chuyển &amp; tham quan)<br />\r\n- Tham quan&nbsp;<em><strong>Khu Du Lịch Đảo Tuần Ch&acirc;u</strong></em>, xem biểu diễn c&aacute; heo - hải cẩu - sư tử biển, xiếc th&uacute;, biểu diễn vũ điệu nhạc nước v&agrave; &aacute;nh s&aacute;ng laser, game trong nh&agrave;, xe điện dụng, xe ngựa h&agrave;o hoa, chiếu phim 5D, triễn l&atilde;m hoa đăng, biễu diễn ca m&uacute;a nhạc.</big></small></h3>\r\n\r\n<h3><small><big>- Tham quan&nbsp;<em><strong>Quần thể Du lịch - Giải tr&iacute; Sun World Hạ Long Park</strong></em>, gồm khu c&ocirc;ng vi&ecirc;n vui chơi ven biển B&atilde;i Ch&aacute;y v&agrave; &nbsp;c&ocirc;ng vi&ecirc;n vui chơi tr&ecirc;n n&uacute;i Ba Đ&egrave;o - được kết nối với nhau bởi hệ thống c&aacute;p treo vượt biển Nữ Ho&agrave;ng đạt 2 kỷ lục thế giới (cabin c&oacute; sức chứa lớn nhất thế giới v&agrave; c&aacute;p treo c&oacute; trụ c&aacute;p cao nhất thế giới so với mặt đất). Trải nghiệm Thế giới tr&ograve; chơi mạo hiểm c&ugrave;ng t&agrave;u lượn si&ecirc;u tốc, c&ocirc;ng vi&ecirc;n nước, khu Vườn Nhật, khu tr&ograve; chơi trong nh&agrave; L&acirc;u Đ&agrave;i Huyền B&iacute;, khu trưng b&agrave;y tượng s&aacute;p, v&agrave; đặc biệt l&agrave; V&ograve;ng quay Mặt Trời - một trong những v&ograve;ng quay cao nhất thế giới.</big></small></h3>\r\n\r\n<h3><small><big><strong>NG&Agrave;Y 5: VỊNH HẠ LONG - BẮC NINH - H&Agrave; NỘI -&nbsp;TP.HCM&nbsp; (Ăn s&aacute;ng, trưa/ Ăn chiều tự t&uacute;c)</strong><br />\r\nBu&ocirc;̉i sáng, l&ecirc;n thuyền du ngoạn<em><strong>&nbsp;vịnh Hạ Long&nbsp;</strong></em>chi&ecirc;m ngưỡng<em><strong>&nbsp;động Thi&ecirc;n Cung, c&aacute;c h&ograve;n Đỉnh Hương - Trống M&aacute;i (G&agrave; Chọi) - Ch&oacute; Đ&aacute;</strong></em>. Khởi h&agrave;nh về H&agrave; Nội. Gh&eacute; Hải Dương, mua đặc sản&nbsp;<strong><em>b&aacute;nh đậu xanh, b&aacute;nh gai</em></strong>. Qua Bắc Ninh, viếng<strong><em>&nbsp;đền Đ&ocirc;&nbsp;</em></strong>- nơi thờ tự 8 vị vua triều L&yacute;. Xe đưa ra s&acirc;n bay Nội B&agrave;i, về TP. Hồ Ch&iacute; Minh (<em><strong>chuyến bay VJ171 L&uacute;c 21:20</strong></em>). Kết th&uacute;c chương tr&igrave;nh (Qu&yacute; kh&aacute;ch tự t&uacute;c phương tiện từ s&acirc;n bay về nh&agrave;.</big></small></h3>', '4 ngày 3 đêm', 30, 'http://localhost:81/NKTravel/public/Image/place1.jpg', 1, 1500000, NULL, 'Miền Bắc'),
(2, 'Du lịch Mỹ', '2019-12-12', '<p><strong>NG&Agrave;Y 1: TP.HCM - HONG KONG&nbsp;- LOS ANGELES - LAS VEGAS (Ăn trưa, tối)</strong><br />\r\nTự t&uacute;c tập trung tại lầu 2 - Ga đi quốc tế, s&acirc;n bay quốc tế T&acirc;n Sơn Nhất. Đ&aacute;p chuyến bay<strong><em>&nbsp;HX539 (04:00 - 08:30)</em></strong>&nbsp;đi Hong Kong. Tiếp tục l&agrave;m thủ tục nối chuyến bay&nbsp;<strong><em>HX068 (11:55 - 10:40) &nbsp;</em></strong>đi Los Angeles, th&agrave;nh phố thi&ecirc;n thần ở miền Nam California với kh&iacute; hậu &ocirc;n h&ograve;a quanh năm. Ăn v&agrave; nghỉ tr&ecirc;n m&aacute;y bay. M&aacute;y bay hạ c&aacute;nh s&acirc;n bay Los Angeles l&uacute;c 10g40 s&aacute;ng (giờ địa phương). Khởi h&agrave;nh đi Las Vegas bằng đường bộ. Nghỉ đ&ecirc;m tại&nbsp;<strong><em>kh&aacute;ch sạn đẳng cấp 5 sao Bellagio&nbsp;</em></strong>(hoặc tương đương). Tự do kh&aacute;m ph&aacute; Las Vegas rực rỡ về đ&ecirc;m.</p>\r\n\r\n<p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<img src=\"https://bizweb.dktcdn.net/100/299/077/products/08.jpg?v=1529553163227\" style=\"height:400px; width:600px\" /></p>\r\n\r\n<p><strong>NG&Agrave;Y 2: LAS VEGAS - ĐẬP THỦY ĐIỆN HOOVER (Ăn s&aacute;ng, trưa, tối)</strong><br />\r\nTham quan<strong><em>&nbsp;đập Hoover</em></strong>&nbsp;h&ugrave;ng vĩ v&agrave; ngắm&nbsp;<strong><em>hồ nước nh&acirc;n tạo Mead</em></strong>&nbsp;xanh r&igrave; (Đập Hoover mang t&ecirc;n vị Tổng thống thứ 31 của nước Mỹ Herbert Hoover, được x&acirc;y dựng với mục ti&ecirc;u cung cấp nguồn điện, điều chỉnh d&ograve;ng chảy v&agrave; kiểm so&aacute;t lũ lụt tr&ecirc;n s&ocirc;ng Colorado). Xe đưa đo&agrave;n chạy dọc theo trục đường nhộn nhịp của Las Vegas để ngắm nh&igrave;n&nbsp;<strong><em>&ldquo;The Strip&rdquo;</em></strong>&nbsp;- con đường d&agrave;i hơn 6km tập trung những kh&aacute;ch sạn v&agrave; casino nổi tiếng v&agrave; sang trọng bậc nhất. Gh&eacute; mua sắm h&agrave;ng hiệu tại&nbsp;<strong><em>Premium Outlet Mall</em></strong>. Nghỉ đ&ecirc;m tại Las Vegas.</p>\r\n\r\n<p><strong>NG&Agrave;Y 3: LAS VEGAS - BARSTOW - LOS ANGELES (&Aacute;n s&aacute;ng, trưa, tối)</strong><br />\r\nTrả ph&ograve;ng. Về lại Los Angeles. Dừng ch&acirc;n tại&nbsp;<strong><em>outlet Barstow</em></strong>&nbsp;nằm ở ph&iacute;a T&acirc;y hoang mạc Mojave để ăn trưa v&agrave; mua sắm. Đến Los Angeles, tự do tham quan v&agrave; dạo chơi&nbsp;<strong><em>khu Little Saigon</em></strong>&nbsp;để t&igrave;m hiểu về cuộc sống của cộng đồng người Việt tại Mỹ. Nghỉ đ&ecirc;m tại Los Angeles (kh&aacute;ch sạn 3 sao Huntington Beach hoặc tương đương).</p>\r\n\r\n<p><strong>NG&Agrave;Y 4: LOS ANGELES - UNIVERSAL STUDIOS HOLLYWOOD (Ăn s&aacute;ng, tối)</strong><br />\r\nKh&aacute;m ph&aacute;&nbsp;<em><strong>Universal Studios</strong></em>&nbsp;- trường quay c&aacute;c bộ phim nổi tiếng của điện ảnh thế giới như King Kong, C&ocirc;ng vi&ecirc;n Khủng long&hellip; Qu&yacute; kh&aacute;ch c&ograve;n c&oacute; dịp được tận mắt chứng kiến c&aacute;c kỹ xảo &acirc;m thanh, kh&oacute;i lửa v&agrave; nước trong c&aacute;c cảnh quay động đất, mưa, nước lũ v&agrave; ch&aacute;y nổ. Xem phim kh&ocirc;ng gian 4D với cảm gi&aacute;c hồi hộp v&agrave; những ấn tượng kh&oacute; qu&ecirc;n về kỷ xảo l&agrave;m phim của điện ảnh Hollywood. Ăn trưa tự t&uacute;c trong c&ocirc;ng vi&ecirc;n. Tham quan trung t&acirc;m kinh đ&ocirc; điện ảnh Hollywood, tản bộ tr&ecirc;n con đường danh vọng Hollywood Boulevard - nơi in t&ecirc;n c&aacute;c t&agrave;i tử nghệ sĩ điện ảnh nổi tiếng như Marilyn Monroe, Michael Jackson. Dừng ch&acirc;n tại nh&agrave; h&aacute;t Chinese Mann&rsquo;s Theatre - nơi in dấu tay của c&aacute;c diễn vi&ecirc;n điện ảnh nổi tiếng. Nghỉ đ&ecirc;m tại Los Angeles.</p>\r\n\r\n<p><strong>NG&Agrave;Y 5: LOS ANGELES - HONG KONG (Ăn s&aacute;ng) &nbsp;</strong><br />\r\nTrả ph&ograve;ng. Xe đưa đo&agrave;n ra s&acirc;n bay Los Angeles đ&aacute;p chuyến bay&nbsp;<em><strong>HX069 (12:40 - 19:00)</strong></em>&nbsp;đi Hong Kong. Ăn v&agrave; nghỉ tr&ecirc;n m&aacute;y bay.</p>\r\n\r\n<p><strong>NG&Agrave;Y 6: HONG KONG - TP. HCM</strong><br />\r\nĐến s&acirc;n bay Hong Kong, đo&agrave;n l&agrave;m thủ tục nối chuyến bay&nbsp;<strong><em>HX538 (23:05 - 00:50)</em></strong>&nbsp;về TP. HCM. M&aacute;y bay đ&aacute;p xuống s&acirc;n bay T&acirc;n Sơn Nhất l&uacute;c 00g50 rạng s&aacute;ng h&ocirc;m sau. Kết th&uacute;c chương tr&igrave;nh du lịch Mỹ.</p>', '10 ngày 9 đêm', 10, 'http://localhost:81/NKTravel/public/Image/place2.jpg', 2, 5000000, 'Châu Mỹ', NULL),
(3, 'Du lịch Châu Âu - Pháp - Thụy Sỹ', '2019-11-27', '<p><strong>NG&Agrave;Y 1: TP.HCM - ABU DHABI (Ăn tối tr&ecirc;n m&aacute;y bay)</strong><br />\r\nQu&yacute; kh&aacute;ch tự t&uacute;c tập trung tại lầu 2 Ga đi quốc tế - s&acirc;n bay T&acirc;n Sơn Nhất, l&agrave;m thủ tục đ&aacute;p chuyến bay&nbsp;<em><strong>EY440 (20:25-00:55) đi Abu Dhabi</strong></em>.</p>\r\n\r\n<p><strong>NG&Agrave;Y 2: ABU DHABI - PARIS (Ăn trưa, tối)</strong><br />\r\nĐến Abu Dhabi, nối chuyến bay<em><strong>&nbsp;EY31 (02:15-07:50) đi Paris</strong></em>. 07h50 s&aacute;ng đ&ecirc;́n s&acirc;n bay Charles De Gaulle. Kh&aacute;m ph&aacute; trung t&acirc;m Paris:&nbsp;<em><strong>Nh&agrave; thờ Đức B&agrave; (Notre Dame de Paris)</strong></em>&nbsp;- c&ocirc;ng tr&igrave;nh kiến tr&uacute;c Gothic ti&ecirc;u biểu v&agrave; nổi tiếng thế giới với t&aacute;c phẩm văn học c&ugrave;ng t&ecirc;n của đại văn h&agrave;o Victor Hugo,&nbsp;<em><strong>Th&aacute;p Eiffel</strong></em>&nbsp;- c&ocirc;ng tr&igrave;nh kiến tr&uacute;c bằng sắt sừng sững b&ecirc;n bờ s&ocirc;ng Seine, biểu tượng v&agrave; niềm tự h&agrave;o của nước Ph&aacute;p.&nbsp;<em><strong>L&ecirc;n tầng 2 của th&aacute;p để chi&ecirc;m ngưỡng thủ đ&ocirc; Paris từ tr&ecirc;n cao. Trải nghiệm du thuyền tr&ecirc;n d&ograve;ng s&ocirc;ng Seine,</strong></em>&nbsp;kh&aacute;m ph&aacute; vẻ đẹp hai b&ecirc;n bờ s&ocirc;ng v&agrave; những c&acirc;y cầu cổ, ti&ecirc;u biểu nhất l&agrave;&nbsp;<em><strong>cầu Alexandre III v&agrave; cầu Neuf&nbsp;</strong></em>- những c&ocirc;ng tr&igrave;nh g&oacute;p phần gi&uacute;p Paris được UNESCO c&ocirc;ng nhận l&agrave; di sản văn h&oacute;a thế giới.&nbsp;<em><strong>Dạo bộ tr&ecirc;n Đại Lộ Thi&ecirc;n Đường</strong></em>&nbsp;(Champs &Eacute;lys&eacute;es) lộng lẫy d&agrave;i gần 2km<em><strong>&nbsp;bắt đầu từ Quảng trường H&ograve;a Hợp (La Concorde) v&agrave; kết th&uacute;c tại Khải Ho&agrave;n M&ocirc;n</strong></em>&nbsp;(L&rsquo;Arc de Triomphe)&nbsp;<em><strong>h&ugrave;ng tr&aacute;ng nằm giữa Quảng trường &Eacute;toile</strong></em>. Nghỉ đ&ecirc;m tại Paris.</p>\r\n\r\n<p><strong>NG&Agrave;Y 3: PARIS - BẢO T&Agrave;NG LOUVRE (Ăn s&aacute;ng, trưa, tối)&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</strong>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<br />\r\nTham quan&nbsp;<em><strong>Bảo t&agrave;ng Louvre</strong></em>&nbsp;- nơi trưng b&agrave;y c&aacute;c b&aacute;u vật hội họa v&agrave; đi&ecirc;u khắc nh&acirc;n loại, nổi tiếng với bức tranh n&agrave;ng Mona Lisa của Leonardo da Vinci với nụ cười b&iacute; ẩn v&agrave; tượng thần Vệ nữ th&agrave;nh Milo - biểu tượng của t&igrave;nh y&ecirc;u v&agrave; sắc đẹp của người Hy Lạp.&nbsp;<em><strong>Tự do mua sắm tại c&aacute;c trung t&acirc;m thương mại lớn, chi&ecirc;m ngưỡng Paris về đ&ecirc;m</strong></em>. Nghỉ đ&ecirc;m tại Paris.</p>\r\n\r\n<p><strong>NG&Agrave;Y 4: PARIS - STRASBOURG - ZURICH (Ăn s&aacute;ng, trưa, tối)</strong><br />\r\nTrả ph&ograve;ng.&nbsp;<em><strong>Đ&aacute;p chuyến t&agrave;u tốc h&agrave;nh tới th&agrave;nh phố Strasbourg s&aacute;t bi&ecirc;n giới Ph&aacute;p - Đức</strong></em>. Kh&aacute;m ph&aacute; một trong những th&agrave;nh phố đẹp nhất của Ph&aacute;p với những ng&ocirc;i nh&agrave; gỗ sơn nhiều m&agrave;u sắc nằm san s&aacute;t ven k&ecirc;nh đ&agrave;o. Chụp h&igrave;nh tại&nbsp;<em><strong>Nh&agrave; thờ Notre Dame</strong></em>&nbsp;mang đậm kiến tr&uacute;c Gothic với những khung cửa sổ lung linh sắc m&agrave;u, những bức tranh kiếng, h&agrave;ng trăm bức tượng, ph&ugrave; đi&ecirc;u trang tr&iacute; v&agrave; cả chiếc đồng hồ thi&ecirc;n văn cổ từ thời Phục Hưng.&nbsp;<em><strong>Dạo chơi khu phố cổ La Petite France</strong></em>, nơi c&oacute; những ng&ocirc;i nh&agrave; h&agrave;ng trăm năm tuổi nằm cạnh c&aacute;c qu&aacute;n c&agrave; ph&ecirc; nhỏ xinh tr&ecirc;n vỉa h&egrave; với những kh&oacute;m hoa quyến rũ bao quanh d&ograve;ng s&ocirc;ng tạo n&ecirc;n khung cảnh thơ mộng. Tiếp tục khởi h&agrave;nh tới&nbsp;<em><strong>Zurich</strong></em>&nbsp;- thủ đ&ocirc; văn h&oacute;a của Thụy Sỹ. Tr&ecirc;n đường đi, tham quan&nbsp;<em><strong>Titisee</strong></em>&nbsp;- thị trấn nằm giữa l&ograve;ng Rừng Đen Black Forest, một địa danh nổi tiếng của nước Đức với những cửa h&agrave;ng đồng hồ Cuckoo.&nbsp;Đến Zurich, đo&agrave;n gh&eacute; chụp h&igrave;nh&nbsp;<em><strong>b&ecirc;n ngo&agrave;i T&ograve;a Thị Ch&iacute;nh Zurich, nh&agrave; thờ Fraum&uuml;nste</strong></em>r - với chuỗi cửa k&iacute;nh m&agrave;u độc đ&aacute;o thể hiện những điển tich trong Kinh Th&aacute;nh một c&aacute;ch đầy m&ecirc; hoặc.&nbsp;<em><strong>Tự do dạo chơi khu phố cổ Altstadl</strong></em>. Nghỉ đ&ecirc;m tại Zurich.</p>\r\n\r\n<p><strong>NGÀY 5: ZURICH - JUNGFRAUJOCH &ndash; INTERLAKEN - GRINDELWALD (Ăn s&aacute;ng, trưa, tối)</strong><br />\r\nKhởi h&agrave;nh đi Grindelwald (144km).&nbsp;<em><strong>Tiếp tục chuyến du ngoạn hấp dẫn &ldquo;Cuộc h&agrave;nh tr&igrave;nh vượt l&ecirc;n đỉnh ch&acirc;u &Acirc;u - n&uacute;i Jungfrau&rdquo; cao 3.454m</strong></em>. Ăn trưa tại nh&agrave; h&agrave;ng tr&ecirc;n đỉnh n&uacute;i. Di chuyển đến&nbsp;<em><strong>Interlaken</strong></em>&nbsp;- th&agrave;nh phố mang đậm n&eacute;t văn h&oacute;a Thụy Sĩ, quanh năm n&aacute;o nhiệt với c&aacute;c lễ hội văn h&oacute;a truyền thống. Nghỉ đ&ecirc;m tại Grindelwald.</p>\r\n\r\n<p><strong>NG&Agrave;Y 6: GRINDELWALD - MILAN (Ăn s&aacute;ng, trưa, tối)</strong><br />\r\nTrả ph&ograve;ng. Di chuyển đến&nbsp;<em><strong>Milan</strong></em>&nbsp;- trung t&acirc;m thời trang của nước &Yacute;. Tham quan&nbsp;<em><strong>Th&aacute;nh đường Duomo Milan</strong></em>&nbsp;thi&ecirc;ng li&ecirc;ng được x&acirc;y dựng từ năm 1386, chụp h&igrave;nh&nbsp;<em><strong>b&ecirc;n ngo&agrave;i l&acirc;u đ&agrave;i Sforza v&agrave; nh&agrave; h&aacute;t La Scala. Tự do dạo chơi tại Galleria Vittorio Emanuele</strong></em>&nbsp;- khu phố thương mại v&agrave; triển l&atilde;m cổ, đặt t&ecirc;n theo Vittorio Emanuele Đệ Nhị, vị vua đầu ti&ecirc;n của Vương quốc &Yacute;. Nghỉ đ&ecirc;m tại Milan.</p>\r\n\r\n<p><strong>NG&Agrave;Y 7: MILAN - VENICE (Ăn s&aacute;ng, trưa, tối)</strong><br />\r\nTrả ph&ograve;ng. Khởi h&agrave;nh đến th&agrave;nh phố<em><strong>&nbsp;Venice</strong></em>&nbsp;- điểm đến l&atilde;ng mạn trong h&agrave;nh tr&igrave;nh &Yacute;. Tự do dạo bước tr&ecirc;n&nbsp;<em><strong>Quảng trường Saint Marco</strong></em>, chụp ảnh tại&nbsp;<em><strong>Cung điện Tổng Trấn Doge&rsquo;s Palace v&agrave; Nh&agrave; thờ m&aacute;i v&ograve;m San Marco</strong></em>&nbsp;- được đặt t&ecirc;n theo vị th&aacute;nh bảo hộ của th&agrave;nh phố. Tham quan&nbsp;<em><strong>xưởng thủy tinh Murano. Trải nghiệm ngồi thuyền gỗ Gondola</strong></em>&nbsp;dập d&igrave;u tr&ecirc;n những k&ecirc;nh đ&agrave;o, tham quan c&aacute;c ng&otilde; ng&aacute;ch của Venice l&atilde;ng mạn (tự t&uacute;c chi ph&iacute;). Nghỉ đ&ecirc;m tại Venice.</p>\r\n\r\n<p><strong>NG&Agrave;Y 8: VENICE - PISA - FLORENCE (Ăn s&aacute;ng, trưa, tối)</strong><br />\r\nTrả ph&ograve;ng. Tham quan&nbsp;<em><strong>Th&aacute;p nghi&ecirc;ng Pisa</strong></em>&nbsp;- c&ocirc;ng tr&igrave;nh kiến tr&uacute;c độc đ&aacute;o nằm tr&ecirc;n&nbsp;<em><strong>Quảng trường Piazza del Miracoli</strong></em>. Tiếp tục h&agrave;nh tr&igrave;nh đến&nbsp;<em><strong>th&agrave;nh phố Florence&nbsp;</strong></em>- c&aacute;i n&ocirc;i của kinh đ&ocirc; nghệ thuật, nơi quy tụ c&aacute;c gi&aacute; trị văn h&oacute;a ti&ecirc;u biểu thời Trung cổ. Chụp h&igrave;nh tại&nbsp;<em><strong>Th&aacute;nh đường Duomo Santa Maria del Fiore</strong></em>&nbsp;nguy nga mang phong c&aacute;ch Gothic v&agrave; chi&ecirc;m ngưỡng<em><strong>&nbsp;bản sao bức tượng Ch&agrave;ng David tại Quảng trường Piazzale Michelangelo</strong></em>. Nghỉ đ&ecirc;m tại Florence.</p>\r\n\r\n<p><strong>NG&Agrave;Y 9: FLORENCE - ROME (Ăn s&aacute;ng, trưa, tối)</strong><br />\r\nTrả ph&ograve;ng. Khởi h&agrave;nh đến th&agrave;nh phố&nbsp;<em><strong>Rome</strong></em>&nbsp;cổ k&iacute;nh - nơi được xem l&agrave; Viện Bảo t&agrave;ng khổng lồ chứa trong l&ograve;ng những đền đ&agrave;i, nh&agrave; thờ, tu viện, quảng trường với nhiều kiến tr&uacute;c cổ vĩ đại. Di chuyển đến Vatican - một quốc gia độc lập ngay giữa th&agrave;nh Rome, với diện t&iacute;ch chưa đến 1km2&nbsp;lại l&agrave; một trong những quốc gia c&oacute; quyền lực nhất thế giới. Tham quan&nbsp;<em><strong>Quảng trường v&agrave; Th&aacute;nh đường St. Peter</strong></em>&nbsp;- nơi thi&ecirc;ng li&ecirc;ng nhất ở Vatican, điểm thu h&uacute;t t&iacute;n đồ v&agrave; du kh&aacute;ch khắp thế giới. Chụp h&igrave;nh&nbsp;<em><strong>b&ecirc;n ngo&agrave;i Đấu trường La M&atilde; Colosseum</strong></em>&nbsp;- minh chứng ki&ecirc;u h&ugrave;ng của nền văn minh La M&atilde; tr&ecirc;n nước &Yacute;. Nghỉ đ&ecirc;m tại Rome.</p>\r\n\r\n<p><strong>NG&Agrave;Y 10: ROME - ABU DHABI (Ăn s&aacute;ng, trưa)</strong><br />\r\nTrả ph&ograve;ng. Dạo bước tr&ecirc;n<em><strong>&nbsp;Quảng trường T&acirc;y Ban Nha v&agrave; chi&ecirc;m ngưỡng Đ&agrave;i phun nước Trevi Fountain</strong></em>&nbsp;- với đ&aacute;y hồ trong veo lấp l&aacute;nh những đồng tiền xu được du kh&aacute;ch quay lưng n&eacute;m xuống với ước mong sẽ quay lại Rome lần nữa. Chụp h&igrave;nh b&ecirc;n ngo&agrave;i&nbsp;<em><strong>Đền Pantheon</strong></em>&nbsp;&ldquo;Ng&ocirc;i đền của c&aacute;c vị thần&rdquo; - đỉnh cao của tư duy kỹ thuật thời La M&atilde; cổ đại. T<em><strong>ự do mua sắm tại trung t&acirc;m thương mại. Ra s&acirc;n bay, đ&aacute;p chuyến bay EY84 (22:00-05:55) đi Abu Dhabi</strong></em>. Ăn tối v&agrave; nghỉ đ&ecirc;m tr&ecirc;n m&aacute;y bay.</p>\r\n\r\n<p><strong>NG&Agrave;Y 11: ABU DHABI - TP.HCM</strong><br />\r\n06g s&aacute;ng đến s&acirc;n bay Abu Dhabi, nối chuyến bay&nbsp;<em><strong>EY441 (08:15-19:05) về Tp. HCM</strong></em>. K&ecirc;́t thúc chương tr&igrave;nh du lịch Ch&acirc;u &Acirc;u.</p>', '9 ngày', 19, 'http://localhost:81/NKTravel/public/Image/place3.jpg', 2, 6000000, 'Châu Âu', NULL),
(4, 'Du lịch Pháp - Bỉ - Hà Lan', '2019-11-26', '<p><strong>NG&Agrave;Y 1: TP.HCM - DOHA (QATAR)</strong><br />\r\nQu&yacute; kh&aacute;ch tự t&uacute;c tập trung tại lầu 2 Ga đi quốc tế - s&acirc;n bay quốc tế T&acirc;n Sơn Nhất, l&agrave;m thủ tục đ&aacute;p chuyến bay<em><strong>&nbsp;QR971 (19:30-23:35) đi Doha - Qatar</strong></em>. Ăn tối tr&ecirc;n m&aacute;y bay.</p>\r\n\r\n<p><strong>Ng&agrave;y 2: DOHA - PARIS (Ăn trưa, tối)</strong><br />\r\nĐến s&acirc;n bay Doha, đo&agrave;n nối chuyến bay&nbsp;<em><strong>QR041 (01:20-07:15) đi Paris</strong></em>. Nghỉ đ&ecirc;m tr&ecirc;n m&aacute;y bay. 07h15 s&aacute;ng đ&ecirc;́n s&acirc;n bay Charles De Gaulle.&nbsp;<em><strong>Kh&aacute;m ph&aacute; trung t&acirc;m Paris</strong></em>&nbsp;- kinh đ&ocirc; &aacute;nh s&aacute;ng, quyến rũ h&agrave;ng triệu kh&aacute;ch du lịch tr&ecirc;n thế giới mỗi năm:&nbsp;<em><strong>Nh&agrave; thờ Đức B&agrave;</strong></em>&nbsp;(Notre Dame de Paris) - c&ocirc;ng tr&igrave;nh kiến tr&uacute;c Gothic ti&ecirc;u biểu v&agrave; nổi tiếng thế giới với t&aacute;c phẩm văn học c&ugrave;ng t&ecirc;n của đại văn h&agrave;o Victor Hugo, tham quan&nbsp;<em><strong>Th&aacute;p Eiffel&nbsp;</strong></em>- c&ocirc;ng tr&igrave;nh kiến tr&uacute;c bằng sắt sừng sững b&ecirc;n bờ s&ocirc;ng Seine, biểu tượng v&agrave; niềm tự h&agrave;o của nước Ph&aacute;p.&nbsp;<em><strong>L&ecirc;n tầng 2 của th&aacute;p để chi&ecirc;m ngưỡng thủ đ&ocirc; Paris - kinh đ&ocirc; &aacute;nh s&aacute;ng từ tr&ecirc;n cao. Trải nghiệm du thuyền tr&ecirc;n d&ograve;ng s&ocirc;ng Seine thơ mộng</strong></em>, kh&aacute;m ph&aacute; vẻ đẹp hai b&ecirc;n bờ s&ocirc;ng v&agrave; những c&acirc;y cầu cổ, ti&ecirc;u biểu nhất l&agrave;&nbsp;<em><strong>cầu Alexandre III v&agrave; cầu Neuf</strong></em>&nbsp;- những c&ocirc;ng tr&igrave;nh g&oacute;p phần gi&uacute;p Paris được UNESCO c&ocirc;ng nhận l&agrave; di sản văn h&oacute;a thế giới. Dạo bộ tr&ecirc;n&nbsp;<em><strong>Đại Lộ Thi&ecirc;n Đường&nbsp;</strong></em>(Champs &Eacute;lys&eacute;es) lộng lẫy d&agrave;i gần 2km bắt đầu từ<em><strong>&nbsp;Quảng trường H&ograve;a Hợp</strong></em>&nbsp;(La Concorde) v&agrave; kết th&uacute;c tại&nbsp;<em><strong>Khải Ho&agrave;n M&ocirc;n</strong></em>&nbsp;(L&rsquo;Arc de Triomphe) h&ugrave;ng tr&aacute;ng nằm giữa<em><strong>&nbsp;Quảng trường &Eacute;toile</strong></em>. Nghỉ đ&ecirc;m tại Paris.</p>\r\n\r\n<p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<img src=\"https://bizweb.dktcdn.net/100/299/077/products/0r2a5723.jpg?v=1529553943837\" style=\"height:400px; width:600px\" /></p>\r\n\r\n<p><strong>Ng&agrave;y 3: PARIS - BẢO T&Agrave;NG LOUVRE (Ăn s&aacute;ng, trưa, tối)&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</strong><br />\r\nTham quan&nbsp;<em><strong>Bảo t&agrave;ng Louvre</strong></em>&nbsp;- nơi trưng b&agrave;y c&aacute;c b&aacute;u vật hội họa v&agrave; đi&ecirc;u khắc nh&acirc;n loại, nổi tiếng với bức tranh n&agrave;ng Mona Lisa của Leonardo da Vinci với nụ cười b&iacute; ẩn v&agrave; tượng thần Vệ nữ th&agrave;nh Milo - biểu tượng của t&igrave;nh y&ecirc;u v&agrave; sắc đẹp của người Hy Lạp. T<em><strong>ự do mua sắm tại c&aacute;c trung t&acirc;m thương mại lớn, chi&ecirc;m ngưỡng Paris về đ&ecirc;m</strong></em>. Nghỉ đ&ecirc;m tại Paris.</p>\r\n\r\n<p><strong>Ng&agrave;y 4: PARIS - BRUSSELS (BỈ) - AMSTERDAM (H&Agrave; LAN) (Ăn s&aacute;ng, trưa, tối)&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;</strong>&nbsp;<br />\r\nTrả ph&ograve;ng. Khởi h&agrave;nh đi<em><strong>&nbsp;Brussels&nbsp;</strong></em>- thủ đ&ocirc; của nước Bỉ, tr&aacute;i tim Ch&acirc;u &Acirc;u - nơi đặt Tổng h&agrave;nh dinh Cộng đồng Ch&acirc;u &Acirc;u (EU) v&agrave; khối NATO. Tham quan&nbsp;<em><strong>Quảng trường Lớn</strong></em>&nbsp;(Grande Place), chụp h&igrave;nh b&ecirc;n ngo&agrave;i&nbsp;<em><strong>M&ocirc; h&igrave;nh Nguy&ecirc;n tử&nbsp;</strong></em>- c&ocirc;ng tr&igrave;nh ti&ecirc;u biểu được x&acirc;y dựng nh&acirc;n dịp Hội chợ triển l&atilde;m Quốc tế năm 1958 tại th&agrave;nh phố.&nbsp;<em><strong>Đi dọc con đường b&aacute;n Chocolate nổi tiếng</strong></em>, chi&ecirc;m ngưỡng<em><strong>&nbsp;tượng Ch&uacute; b&eacute; T&egrave;</strong></em>&nbsp;(Manneken Pis) - biểu tượng kh&ocirc;ng ch&iacute;nh thức của Brussels. Về Amsterdam, tham quan chụp h&igrave;nh tại&nbsp;<em><strong>Đ&agrave;i tưởng niệm H&ograve;a B&igrave;nh nằm tr&ecirc;n Quảng trường Dam</strong></em>. Nghỉ đ&ecirc;m tại Amsterdam.</p>\r\n\r\n<p><strong>Ng&agrave;y 5: AMSTERDAM -&nbsp;LỄ HỘI HOA KEUKENHOF 2018 (Ăn s&aacute;ng, trưa, tối)</strong><br />\r\nXe đưa đo&agrave;n đi&nbsp;<em><strong>tham dự lễ hội hoa Keukenhof 2018</strong></em>, điểm nhấn đặc sắc trong chương tr&igrave;nh tour du lịch ch&acirc;u &Acirc;u m&ugrave;a lễ hội. Qu&yacute; kh&aacute;ch tận mắt chi&ecirc;m ngưỡng sắc m&agrave;u của h&agrave;ng ng&agrave;n lo&agrave;i hoa Tulip v&agrave; c&aacute;c giống hoa đến từ c&aacute;c ch&acirc;u lục kh&aacute;c được trưng b&agrave;y như tấm thảm hoa tr&ecirc;n diện t&iacute;ch 32 hecta ph&iacute;a Nam Amsterdam. Tiếp tục di chuyển đến&nbsp;<em><strong>khu đồng qu&ecirc; Zaanse Schans</strong></em>&nbsp;- điểm nhấn đặc sắc trong chương tr&igrave;nh tour du lịch ch&acirc;u &Acirc;u. Qu&yacute; kh&aacute;ch c&oacute; cơ hội đắm m&igrave;nh trong kh&ocirc;ng gian y&ecirc;n b&igrave;nh của l&agrave;ng qu&ecirc; với những chiếc cối xay gi&oacute;, c&ocirc; g&aacute;i H&agrave; Lan trong trang phục truyền thống, tham quan xưởng l&agrave;m guốc gỗ truyền thống v&agrave; sản xuất pho-mai. Nghỉ đ&ecirc;m tại Amsterdam.</p>\r\n\r\n<p><strong>Ng&agrave;y 6: AMSTERDAM - DOHA - TP.HCM (Ăn s&aacute;ng)</strong><br />\r\nTrả ph&ograve;ng. Tham quan&nbsp;<em><strong>Trung t&acirc;m chế t&aacute;c kim cương Amsterdam v&agrave; du thuyền tr&ecirc;n s&ocirc;ng Amstel&nbsp;</strong></em>ngắm nh&igrave;n những c&acirc;y cầu l&atilde;ng mạn v&agrave; những nếp nh&agrave; cổ k&iacute;nh rực rỡ sắc hoa b&ecirc;n cửa sổ. Ra s&acirc;n bay, đ&aacute;p chuyến bay&nbsp;<em><strong>QR274 (16:40 - 01:05) đi Doha</strong></em>. Ăn tối tr&ecirc;n m&aacute;y bay.</p>\r\n\r\n<p><strong>NG&Agrave;Y 7: DOHA - TP.HCM</strong><br />\r\nĐến s&acirc;n bay Doha, nối chuyến bay&nbsp;<em><strong>QR970 (02:15-13:55) về Tp.HCM</strong></em>. Kết th&uacute;c chương tr&igrave;nh tour Ch&acirc;u &Acirc;u.</p>', '7 ngày 6 đêm', 20, 'http://localhost:81/NKTravel/public/Image/place4.jpg', 2, 7000000, 'Châu Âu', NULL),
(5, 'Du lịch Đà Nẵng - KDL Bà Nà - Hội An', '2019-11-07', '<p><strong>NG&Agrave;Y 1: HCM - Đ&Agrave; NẴNG - HỘI AN (Ăn trưa, chiều)</strong><br />\r\n10:00 Xe v&agrave; hướng dẫn vi&ecirc;n Saigontourist đ&oacute;n Qu&yacute; kh&aacute;ch tại 55B Phan Chu Trinh, H&agrave; Nội đưa ra s&acirc;n bay Nội B&agrave;i đ&aacute;p chuyến bay&nbsp;<em><strong>VN173 (DKCC 12:30)/VN175 (DKCC 14:00)&nbsp;&nbsp;</strong></em>&nbsp;đi Đ&agrave; Nẵng. (Ăn trưa tự t&uacute;c). Tới s&acirc;n bay Đ&agrave; Nẵng xe đưa qu&yacute; kh&aacute;ch về nhận ph&ograve;ng kh&aacute;ch sạn. 15:00 Khởi h&agrave;nh đi Hội An, thăm&nbsp;<strong><em>phố cổ Hội An, Hội Qu&aacute;n Ph&uacute;c Kiến, ch&ugrave;a Cầu Nhật Bản, khu phố Đ&egrave;n Lồng</em></strong>&nbsp;chi&ecirc;m ngưỡng cuộc sống b&igrave;nh dị v&agrave; ấm c&uacute;ng của người d&acirc;n Phố Cổ. Ăn tối tại Hội An.Tự do tham quan v&agrave; vui chơi giải tr&iacute; tại C&ocirc;ng vi&ecirc;n Asean Park . Nghỉ đ&ecirc;m tại Đ&agrave; Nẵng.</p>\r\n\r\n<p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;<img src=\"https://bizweb.dktcdn.net/100/299/077/files/du-lich-hue-co-gi-dep-h2.jpg?v=1529554042861\" style=\"height:400px; width:600px\" /></p>\r\n\r\n<p><strong>NG&Agrave;Y 2: Đ&Agrave; NẴNG - KHU DU LỊCH B&Agrave; N&Agrave; (Ăn s&aacute;ng, trưa, chiều)</strong><br />\r\nQu&yacute; kh&aacute;ch đi&nbsp;<em><strong>khu du lịch B&agrave; N&agrave; - N&uacute;i Ch&uacute;a</strong></em>&nbsp;theo tuyến đường du lịch Nguyễn Tất Th&agrave;nh - tuyến đường ven biển đẹp nhất Miền Trung, ngắm cầu treo Thuận Phước - c&acirc;y cầu d&acirc;y võng d&agrave;i nhất Việt Nam. L&ecirc;n đỉnh&nbsp;<em><strong>B&agrave; N&agrave;&nbsp;</strong></em>bằng hệ thống c&aacute;p treo đạt 2 kỷ lục Guinness, ngắm to&agrave;n cảnh n&uacute;i non h&ugrave;ng vỹ v&agrave; tận hưởng kh&iacute; hậu trong l&agrave;nh, tươi m&aacute;t. (chi ph&iacute; c&aacute;p treo tự t&uacute;c). Đến B&agrave; N&agrave;, tham quan<strong><em>&nbsp;ch&ugrave;a Linh Ứng,</em></strong>&nbsp;kh&aacute;m ph&aacute; khu vui chơi - giải tr&iacute; trong nh&agrave; mang đẳng cấp quốc tế&nbsp;<strong><em>Fantasy Park</em></strong>&nbsp;- nơi c&oacute; rất nhiều tr&ograve; chơi từ vui nhộn đến cảm gi&aacute;c mạnh, ph&ugrave; hợp mọi lứa tuổi. Ăn trưa tại B&agrave; N&agrave;. Sau đ&oacute; quay về trung t&acirc;m, tự do nghỉ ngơi tắm biển. Ăn tối &amp; nghỉ đ&ecirc;m tại Đ&agrave; Nẵng.</p>\r\n\r\n<p><strong>NG&Agrave;Y 3: Đ&Agrave; NẴNG - HUẾ (Ăn s&aacute;ng, trưa, chiều)</strong><br />\r\nĂn s&aacute;ng tại kh&aacute;ch sạn, xe đưa qu&yacute; kh&aacute;ch tham quan&nbsp;<strong><em>Bảo T&agrave;ng Đ&agrave; Nẵng, Th&agrave;nh Điện Hải&nbsp;</em></strong>- nơi mở đầu cuộc x&acirc;m lược của Thực d&acirc;n Ph&aacute;p v&agrave;o Việt Nam, &nbsp;chụp h&igrave;nh kỷ niệm tại Trung t&acirc;m H&agrave;nh ch&iacute;nh TP. Đ&agrave; Nẵng,&nbsp;<strong><em>Cầu Rồng, S&ocirc;ng H&agrave;n</em></strong>. Ăn trưa, khởi h&agrave;nh đi Huế. Tới nơi, nhận ph&ograve;ng kh&aacute;ch sạn, tự do nghỉ ngơi. Sau bữa tối, đo&agrave;n l&ecirc;n Thuyền đi Nghe<strong><em>&nbsp;Ca Huế tr&ecirc;n s&ocirc;ng Hương</em></strong>. Nghỉ đ&ecirc;m tại Huế.</p>\r\n\r\n<p><strong>NG&Agrave;Y 4: HU&Ecirc; - H&Agrave; NỘI (Ăn s&aacute;ng, trưa, tối)</strong><br />\r\nĂn s&aacute;ng tại kh&aacute;ch sạn, sau đ&oacute; tự do nghỉ ngơi hoặc đi&nbsp;<em><strong>chợ Đ&ocirc;ng Ba</strong></em>&nbsp;mua đặc sản xứ Huế về l&agrave;m qu&agrave; cho người th&acirc;n. Sau bữa trưa, xe đưa đo&agrave;n đi tham quan Kinh Th&agrave;nh Huế với<strong><em>&nbsp;Ngọ M&ocirc;n, Cửu Đỉnh, điện Th&aacute;i H&ograve;a, Ch&ugrave;a Thi&ecirc;n Mụ</em></strong>. Ăn tối tại Nh&agrave; h&agrave;ng. Sau đ&oacute; tiễn đo&agrave;n ra s&acirc;n bay Ph&uacute; B&agrave;i đ&aacute;p chuyến bay về H&agrave; Nội&nbsp;<strong><em>(VN1546 dự kiến Cc 20:50)</em></strong>. Về đến s&acirc;n bay Nội B&agrave;i, xe v&agrave; HDV đ&oacute;n qu&yacute; kh&aacute;ch trở về điểm hẹn ban đầu. Kết th&uacute;c chuyến tham quan.</p>', '6 ngày', 50, 'http://localhost:81/NKTravel/public/Image/place5.jpg', 1, 1200000, NULL, 'Miền Trung'),
(6, 'Du lịch  Nha Trang - Hòn Lao', '2019-12-13', '<p><strong>NG&Agrave;Y 1: TP. HỒ CH&Iacute; MINH - NHA TRANG&nbsp;(Ăn trưa,&nbsp;chiều)</strong><br />\r\nBuổi s&aacute;ng, qu&yacute; kh&aacute;ch tập trung tại - Ga đi trong nước, s&acirc;n bay T&acirc;n Sơn Nhất. HDV Saigontourist đ&oacute;n v&agrave; hỗ trợ qu&yacute; kh&aacute;ch l&agrave;m thủ tục. Khởi h&agrave;nh đi Nha Trang (<em><strong>chuy&ecirc;́n bay VJ602 l&uacute;c 07:30</strong></em>). Tới Cam Ranh, xe đưa đo&agrave;n v&agrave;o Nha Trang. Dừng tham quan&nbsp;<em><strong>Khu tưởng niệm chiến sĩ Gạc Ma</strong></em>, nơi tưởng nhớ 64 chiến sĩ Hải qu&acirc;n Nh&acirc;n d&acirc;n Việt Nam đ&atilde; hi sinh khi bảo vệ chủ quyền Tổ quốc tại đảo Gạc Ma ng&agrave;y 14/03/1988. Đ&ecirc;́n Nha Trang, tham quan<em><strong>&nbsp;Trung t&acirc;m suối kho&aacute;ng n&oacute;ng I resort Nha Trang</strong></em>. Tự do thư gi&atilde;n v&agrave; tắm kho&aacute;ng (tự túc chi phí tắm bùn), chi&ecirc;m ngưỡng thắng cảnh&nbsp;<strong><em>Hòn Ch&ocirc;̀ng và Núi C&ocirc; Ti&ecirc;n</em></strong>. Viếng<strong><em>&nbsp;th&aacute;p B&agrave; Ponagar&nbsp;</em></strong>- ng&ocirc;i đ&ecirc;̀n c&ocirc;̉ được người Chăm x&acirc;y dựng từ th&ecirc;́ kỷ thứ VIII. Buổi chiều tối, qu&yacute; kh&aacute;ch tự do kh&aacute;m ph&aacute; th&agrave;nh phố biển Nha Trang. Nghỉ đ&ecirc;m tại Nha Trang.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>NG&Agrave;Y 2: NHA TRANG - KDL H&Ograve;N LAO&nbsp;(Ăn s&aacute;ng, trưa / Ăn chiều tự t&uacute;c)</strong><br />\r\nBuổi s&aacute;ng, l&ecirc;n thuyền tới&nbsp;<strong><em>khu du lịch H&ograve;n Lao - đảo Khỉ</em></strong>, khu du lịch sinh th&aacute;i nổi tiếng tại Nha Trang. Tắm biển, thư gi&atilde;n, xem xiếc khỉ, xiếc ch&oacute;, d&ecirc;, đua ch&oacute; hoặc thử c&aacute;c m&ocirc;n thể thao b&atilde;i biển: can&ocirc; k&eacute;o d&ugrave;, m&ocirc;t&ocirc; nước... (tự t&uacute;c chi ph&iacute;). Về lại đất liền. Trở về khách sạn, nghỉ ngơi. Bu&ocirc;̉i chi&ecirc;̀u, &nbsp;tự do dạo chơi ph&ocirc;́ bi&ecirc;̉n, tự t&uacute;c ăn chiều, Nghỉ đ&ecirc;m tại Nha Trang.</p>\r\n\r\n<p><strong>Lựa chọn</strong>&nbsp;(tự t&uacute;c chi ph&iacute; di chuyển, tham quan &amp; ăn chiều)<br />\r\n- Tham quan&nbsp;<strong><em>Khu vui chơi giải tr&iacute; Vinpearl Land&nbsp;</em></strong>(chi ph&iacute; tự t&uacute;c) với nhiều hoạt động hấp dẫn: Sang đảo bằng hệ thống c&aacute;p treo vượt biển; Kh&aacute;m ph&aacute; Thủy cung lớn v&agrave; hiện đại nhất Việt Nam với h&agrave;ng trăm lo&agrave;i sinh vật biển; Tham gia c&aacute;c tr&ograve; chơi cảm gi&aacute;c mạnh tại Khu tr&ograve; chơi trong nh&agrave;, Khu tr&ograve; chơi ngo&agrave;i trời, C&ocirc;ng vi&ecirc;n nước ngọt tr&ecirc;n b&atilde;i biển đầu ti&ecirc;n tại Việt Nam; Xem phim 4D với c&aacute;c hiệu ứng mới lạ, đặc biệt; Thưởng thức chương tr&igrave;nh biểu diễn nhạc nước s&ocirc;i động ho&agrave;nh tr&aacute;ng; Dạo bộ dọc khu phố mua sắm.</p>\r\n\r\n<p><strong>NG&Agrave;Y 3: NHA TRANG - TP. HỒ CH&Iacute; MINH (Ăn s&aacute;ng, trưa)</strong><br />\r\nBuổi s&aacute;ng,&nbsp;tự do kh&aacute;m ph&aacute; th&agrave;nh phố biển Nha Trang. Trả ph&ograve;ng. Tham quan<em><strong>&nbsp;viện Hải dương học</strong></em>, viếng<em><strong>&nbsp;Long Sơn tự</strong></em>, chi&ecirc;m b&aacute;i&nbsp;<strong><em>Kim Th&acirc;n Phật Tổ</em></strong>. Mua sắm tại&nbsp;<em><strong>chợ Đầm</strong></em>. Tiễn ra s&acirc;n bay Cam Ranh, về TP. HCM (<strong><em>chuy&ecirc;́n bay VJ609 l&uacute;c 15:40</em></strong>). Kết th&uacute;c chương tr&igrave;nh. (Qu&yacute; kh&aacute;ch tự t&uacute;c phương tiện từ s&acirc;n bay về lại nh&agrave;).</p>\r\n\r\n<p><strong>Ghi ch&uacute;:</strong><br />\r\n- Điểm tham quan c&oacute; thể sắp xếp lại cho ph&ugrave; hợp m&agrave; vẫn bảo đảm đầy đủ nội dung của từng chương tr&igrave;nh<br />\r\n- Giờ bay c&oacute; thể bị thay đổi bởi Vietjet Air</p>', '3 ngày 2 đêm', 30, 'http://localhost:81/NKTravel/public/Image/place6.jpg', 1, 1300000, NULL, 'Miền Trung'),
(7, 'Du lịch khám phá Cái Bè - Cần Thơ - Châu Đốc', '2019-11-22', '<p><strong>Ng&agrave;y 01: TP. HỒ CH&Iacute; Minh - C&Aacute;I B&Egrave; - CẦN THƠ&nbsp; (Ăn s&aacute;ng, trưa, chiều)</strong><br />\r\nĐ&oacute;n kh&aacute;ch tại văn ph&ograve;ng Saigontourist. Đến C&aacute;i B&egrave;, tham quan&nbsp;<strong><em>chợ nổi C&aacute;i B&egrave;</em></strong>. T&igrave;m hiểu qui tr&igrave;nh l&ograve; cốm, kẹo dừa, nu&ocirc;i ong v&agrave; thưởng thức tr&agrave; mật ong. L&ecirc;n c&ugrave; lao An B&igrave;nh tham quan&nbsp;<strong><em>nh&agrave; xưa Nam Bộ</em></strong>, tham quan vườn ươm c&acirc;y giống c&aacute;c loại c&acirc;y ăn tr&aacute;i nhiệt đới. Nhận ph&ograve;ng v&agrave; nghỉ đ&ecirc;m tại Cần Thơ.</p>\r\n\r\n<p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<img src=\"https://bizweb.dktcdn.net/100/299/077/products/ha-tien-2919-1200x800.jpg?v=1529553448983\" style=\"height:400px; width:600px\" /></p>\r\n\r\n<p><strong>Ng&agrave;y 02 : CẦN THƠ - CH&Acirc;U ĐỐC (Ăn s&aacute;ng, trưa, chiều)</strong><br />\r\nBuổi s&aacute;ng, đi thuyền tham quan&nbsp;<em><strong>chợ nổi C&aacute;i Răng</strong></em>, viếng&nbsp;<strong><em>Thiền viện Tr&uacute;c L&acirc;m Phương Nam</em></strong>. Khởi h&agrave;nh điCh&acirc;u Đốc. Buổi chiều viếng&nbsp;<strong><em>Miếu B&agrave; Ch&uacute;a Xứ, T&acirc;y An cổ tự, Lăng Thoại Ngọc Hầu, Ch&ugrave;a Hang.</em></strong>&nbsp;Nghỉ đ&ecirc;m tại Ch&acirc;u Đốc.</p>\r\n\r\n<p><strong>Ng&agrave;y 03: CH&Acirc;U ĐỐC - H&Agrave; TI&Ecirc;N (Ăn s&aacute;ng, trưa, chiều)</strong><br />\r\nKhởi h&agrave;nh đi H&agrave; Ti&ecirc;n. Tr&ecirc;n đường tham quan&nbsp;<strong><em>rừng tr&agrave;m Tr&agrave; Sư</em></strong>, theo những con rạch xuy&ecirc;n qua Lung Sen v&agrave; khu Rừng Giống, l&ecirc;n th&aacute;p vọng cảnh cao 23m nh&igrave;n to&agrave;n cảnh rừng tr&agrave;m v&agrave;<strong><em>&nbsp;tượng Phật Di Lặc, ch&ugrave;a Vạn Linh</em></strong>&nbsp;tr&ecirc;n n&uacute;i Cấm bằng k&iacute;nh viễn vọng. Đi H&agrave; Ti&ecirc;n dọc k&ecirc;nh Vĩnh Tế. Viếng<strong><em>&nbsp;lăng Mạc Cửu</em></strong>; tham quan&nbsp;<em><strong>Thạch Động</strong></em>, b&atilde;i biển&nbsp;<strong><em>Mũi Nai.</em></strong>&nbsp; Nghỉ đ&ecirc;m tại H&agrave; Ti&ecirc;n.</p>\r\n\r\n<p><strong>Ng&agrave;y 04: H&Agrave; TI&Ecirc;N - TP. HỒ CH&Iacute; Minh &nbsp;(Ăn s&aacute;ng, trưa)</strong><br />\r\nBuổi s&aacute;ng khởi h&agrave;nh về TP. HCM. Đến TP.HCM, đưa qu&yacute; kh&aacute;ch về văn ph&ograve;ng Saigontourist. Kết th&uacute;c chương tr&igrave;nh.&nbsp;</p>', '5 ngày 4 đêm', 20, 'http://localhost:81/NKTravel/public/Image/place7.jpg', 1, 5400000, NULL, 'Miền Nam'),
(8, 'Du lịch Phú Quốc - Bãi Sao - VinPearl Land', '2020-01-09', '<p><strong>NG&Agrave;Y 1: TP. HỒ CH&Iacute; MINH - PH&Uacute; QUỐC (Ăn s&aacute;ng, trưa / Ăn chiều tự t&uacute;c)</strong><br />\r\nBuổi s&aacute;ng, qu&yacute; kh&aacute;ch tập trung tại Ga đi trong nước, s&acirc;n bay T&acirc;n Sơn Nhất. Khởi h&agrave;nh đi Ph&uacute; Quốc (<em><strong>chuyến VJ321 l&uacute;c 06:55</strong></em>). Đến Ph&uacute; Quốc, đo&agrave;n khởi h&agrave;nh đi ph&iacute;a Đ&ocirc;ng đảo, tham quan điểm&nbsp;<strong><em>Trại ch&oacute; xo&aacute;y Ph&uacute; Quốc, Vườn ti&ecirc;u, L&ograve; chế biến rượu Sim rừng Ph&uacute; Quốc, Cơ sở nước mắm Ph&uacute; Quốc</em></strong>. Nhận ph&ograve;ng. Buổi chiều tối tự do dạo<strong><em>&nbsp;chợ đ&ecirc;m Dinh Cậu</em></strong>, tự t&uacute;c ăn chiều. Nghỉ đ&ecirc;m tại Ph&uacute; Quốc.</p>\r\n\r\n<p><strong>Lựa chọn&nbsp;</strong>(tự t&uacute;c chi ph&iacute; tham quan, di chuyển v&agrave; ăn chiều):<br />\r\n- Tham quan&nbsp;<em><strong>Khu vui chơi giải tr&iacute; Vinpearl Land&nbsp;Ph&uacute; Quốc&nbsp;</strong></em>với c&aacute;c tr&ograve; chơi trong nh&agrave;, tr&ograve; chơi ngo&agrave;i trời Đĩa quay si&ecirc;u tốc, Cối xay gi&oacute;, Đĩa bay&hellip;, L&acirc;u đ&agrave;i Cổ t&iacute;ch; Khu c&ocirc;ng vi&ecirc;n nước&nbsp;với c&aacute;c tr&ograve; chơi mạo hiểm: Boomerang khổng lồ, Đường trượt si&ecirc;u l&ograve;ng chảo&hellip;; Phim 5D với nhiều kỹ xảo v&agrave; hiệu ứng hiện đại; Khu Thủy Cung&nbsp;với h&agrave;ng trăm lo&agrave;i sinh vật biển qu&yacute; hiếm: chim C&aacute;nh cụt,&nbsp; c&aacute; Nemo, c&aacute; Napoleon, c&aacute; Mập trắng, cua King Crab&hellip;Thưởng thức chương tr&igrave;nh biểu diễn nhạc nước, biểu diễn c&aacute; heo, n&agrave;ng ti&ecirc;n c&aacute;,&hellip;</p>\r\n\r\n<p>- Kh&aacute;m ph&aacute;&nbsp;<strong><em>Khu Vinpearl Safari</em></strong>: kh&aacute;m ph&aacute; Vườn Th&uacute; Hoang D&atilde; đầu ti&ecirc;n tại Việt Nam với quy m&ocirc; 180ha, c&ugrave;ng hơn 130 lo&agrave;i động vật qu&yacute; hiếm v&agrave; c&aacute;c chương tr&igrave;nh Biểu diễn động vật, Chụp ảnh với động vật, Kh&aacute;m ph&aacute; v&agrave; trải nghiệm Vườn th&uacute; mở trong rừng tự nhi&ecirc;n, gần gũi v&agrave; th&acirc;n thiện với con người</p>\r\n\r\n<p>- Ng&acirc;m tắm b&ugrave;n kho&aacute;ng n&oacute;ng tại<strong><em>&nbsp;Galina Ph&uacute; Quốc</em></strong>&nbsp;- trung t&acirc;m b&ugrave;n kho&aacute;ng đầu ti&ecirc;n tại Ph&uacute; Quốc. thư gi&atilde;n dưới lớp b&ugrave;n kho&aacute;ng được khai th&aacute;c từ mỏ b&ugrave;n ở Ninh H&ograve;a (Kh&aacute;nh H&ograve;a), được xử l&yacute; qua c&ocirc;ng nghệ li t&acirc;m lọc chọn những lớp b&ugrave;n kho&aacute;ng tốt nhất mang tới cho Qu&yacute; kh&aacute;ch. Ngo&agrave;i ra c&ograve;n c&oacute; c&aacute;c dịch vụ chăm s&oacute;c sức khỏe như: massage, hệ thống &ocirc;n tuyền thủy liệu ph&aacute;p, bể jaccuzi</p>\r\n\r\n<p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;<img src=\"https://bizweb.dktcdn.net/100/299/077/products/du-l-ch-c-n-o-5.jpg?v=1529553550823\" style=\"height:400px; width:600px\" /></p>\r\n\r\n<p><strong>NG&Agrave;Y 2: THAM QUAN PH&Uacute; QUỐC&nbsp;(Ăn s&aacute;ng, trưa, chiều)</strong><br />\r\nĐo&agrave;n tham quan<strong><em>&nbsp;trung t&acirc;m nu&ocirc;i cấy ngọc trai Ph&uacute; Quốc, di tich nh&agrave; t&ugrave; Ph&uacute; Quốc</em></strong>, viếng&nbsp;<strong><em>Thi&ecirc;n Viện Tr&uacute;c L&acirc;m Hộ Quốc</em></strong>&nbsp;ng&ocirc;i ch&ugrave;a đẹp v&agrave; lớn nhất đảo ngọc. Tắm&nbsp;<em><strong>biển B&atilde;i Sao</strong></em>&nbsp;(hoặc b&atilde;i &Ocirc;ng Lang t&ugrave;y điều kiện thời tiết) - một trong những b&atilde;i biển đẹp nh&acirc;́t tại Ph&uacute; Quốc. Buổi chiều đo&agrave;n viếng&nbsp;<strong><em>dinh Bà</em></strong>&nbsp;- Thủy Long Th&aacute;nh Mẫu (Thần Nước), chi&ecirc;m ngưỡng vẻ đẹp của thắng cảnh dinh Cậu. Trở về kh&aacute;ch sạn nghỉ ngơi. Tối sinh hoạt tự do</p>\r\n\r\n<p><strong>NG&Agrave;Y 3: PH&Uacute; QUỐC - TP. HỒ CH&Iacute; MINH (Ăn s&aacute;ng, trưa)</strong><br />\r\nQu&yacute; kh&aacute;ch tự do tắm biển Ph&uacute; Quốc, trả ph&ograve;ng. Xe đưa qu&yacute; kh&aacute;ch ra s&acirc;n bay Ph&uacute; Quốc bay về Tp.Hồ Ch&iacute; Minh (<strong><em>chuyến VJ 326 l&uacute;c 16:50</em></strong>). Kết th&uacute;c chương tr&igrave;nh. (Qu&yacute; kh&aacute;ch tự t&uacute;c phương tiện từ s&acirc;n bay về lại nh&agrave;).&nbsp;</p>', '3 ngày 2 đêm', 17, 'http://localhost:81/NKTravel/public/Image/place8.jpg', 1, 1400000, NULL, 'Miền Nam'),
(9, 'Du lịch Nha Trang - Hòn Lao - Dốc Lết', '2019-11-30', '<p><strong>Ng&agrave;y 01: TP. HỒ CH&Iacute; MINH - NHA TRANG &nbsp;(Ăn s&aacute;ng, trưa, chiều)</strong><br />\r\nĐ&oacute;n kh&aacute;ch tại văn ph&ograve;ng Saigontourist, khởi h&agrave;nh đi Nha Trang. Tr&ecirc;n đường đi, chi&ecirc;m ngưỡng vẻ đẹp của B&atilde;i biển c&aacute;t trắng C&agrave; N&aacute;, tham quan&nbsp;<em><strong>Khu tưởng niệm chiến sĩ Gạc Ma</strong></em>. du kh&aacute;ch nhận ph&ograve;ng. Nghỉ đ&ecirc;m tại Nha Trang.</p>\r\n\r\n<p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;<img src=\"https://bizweb.dktcdn.net/100/299/077/products/ponagar-cham-temple-nha-trang-min1.jpg?v=1529554507043\" style=\"height:400px; width:600px\" /></p>\r\n\r\n<p><strong>Ng&agrave;y 02: NHA TRANG - H&Ograve;N LAO - I RESORT NHA TRANG &nbsp;(Ăn s&aacute;ng, trưa, chiều)</strong><br />\r\nBuổi s&aacute;ng, l&ecirc;n thuyền tới khu du lịch<em><strong>&nbsp;H&ograve;n Lao - đảo Khỉ</strong></em>, tắm biển, thư gi&atilde;n, xem xiếc khỉ, xiếc ch&oacute;, d&ecirc;, đua ch&oacute; hoặc thử c&aacute;c m&ocirc;n thể thao b&atilde;i biển: can&ocirc; k&eacute;o d&ugrave;, m&ocirc;t&ocirc; nước... (chi ph&iacute; tự t&uacute;c). Về lại đất liền, tham quan Trung t&acirc;m suối kho&aacute;ng n&oacute;ng Iresort Nha Trang. Du kh&aacute;ch tự do thư gi&atilde;n v&agrave; tắm kho&aacute;ng. Nghỉ đ&ecirc;m tại Nha Trang.</p>\r\n\r\n<p><strong>Ng&agrave;y 03: NHA TRANG &ndash; DỐC LẾT - C&Aacute;C THẮNG CẢNH (Ăn s&aacute;ng, trưa)</strong><br />\r\nKhởi h&agrave;nh đi Dốc Lết. Tự do tham quan v&agrave; tắm biển. Chiều kh&aacute;ch đi tham quan c&aacute;c thắng cảnh nổi tiếng&nbsp;<strong><em>h&ograve;n Chồng</em></strong>, ngắm cảnh&nbsp;<em><strong>n&uacute;i C&ocirc; Ti&ecirc;n</strong></em>. Viếng&nbsp;<strong><em>th&aacute;p B&agrave; Ponagar</em></strong>, tham quan v&agrave; mua sắm tại&nbsp;<strong><em>chợ Đầm</em></strong>. Tự t&uacute;c ăn tối. Nghỉ đ&ecirc;m tại Nha Trang.</p>\r\n\r\n<p><strong>Lựa chọn (tự t&uacute;c chi ph&iacute; di chuyển v&agrave; tham quan):</strong>&nbsp;Qu&yacute; kh&aacute;ch c&oacute; thể tham quan Khu vui chơi giải tr&iacute; Vinpearl Land hệ thống c&aacute;p treo vượt biển d&agrave;i nhất thế giới; Thủy cung lớn v&agrave; hiện đại nhất Việt Nam; Khu tr&ograve; chơi trong nh&agrave; hoặc chinh phục thử th&aacute;ch cao độ từ h&agrave;ng chục tr&ograve; chơi cảm gi&aacute;c mạnh tại Khu tr&ograve; chơi ngo&agrave;i trời v&agrave; C&ocirc;ng vi&ecirc;n nước ngọt tr&ecirc;n b&atilde;i biển đầu ti&ecirc;n &amp; duy nhất tại Việt Nam; ph&ograve;ng chiếu phim 4D, chương tr&igrave;nh biểu diễn nhạc nước</p>\r\n\r\n<p><strong>Ng&agrave;y 04: NHA TRANG -&nbsp;TP. HỒ CH&Iacute;&nbsp; MINH (Ăn s&aacute;ng, trưa)</strong><br />\r\nTrả ph&ograve;ng, khởi h&agrave;nh về th&agrave;nh phố. Tr&ecirc;n đường dừng ch&acirc;n tại Phan Rang nghỉ giải lao v&agrave; mua đặc sản Ninh Thuận. Đến Tp. Hồ Ch&iacute; Minh, kết th&uacute;c chuyến tham quan.</p>', '8 ngày 7 đêm', 16, 'http://localhost:81/NKTravel/public/Image/place9.jpg', 1, 900000, NULL, 'Miền Trung');
